String[] testWords = ["cocktaiil", "whitewone", "biir"]
String[] rightWords = ["cocktail", "white wine", "beer"]
size = new Integer(testWords.size())
i = 0
while (i < size){
	String testWord = testWords[i];
	testRunner.testCase.setPropertyValue("TestWord", testWord)
	String rightWord = rightWords[i];
	testRunner.testCase.setPropertyValue("RightWord", rightWord)
	testRunner.runTestStepByName("SOAP RequestGroovy")
	i++;
}
